mapper.select:
SELECT sdet.event_type AS eventType, sdt.tunnel_name AS tunnelName, sde.event_time AS startTime, sde.event_title AS eventDescription, sde.event_state AS eventState FROM sd_event sde LEFT JOIN sd_event_type sdet ON sde.event_type_id = sdet.id LEFT JOIN sd_tunnels sdt ON sde.tunnel_id = sdt.tunnel_id WHERE sde.event_time > DATE_ADD( curdate(), INTERVAL - DAY ( curdate())+ 1 DAY ) AND sde.event_time < last_day( curdate()) AND sdet.is_usable = '1'
AND sde.tunnel_id = #{tunnelId}
ORDER BY sde.event_time DESC LIMIT 50

SELECT sdd.dict_value, IFNULL( es.percentage, '0' ) AS percentage FROM sys_dict_data sdd LEFT JOIN ( SELECT event_state AS eventState, ROUND( ROUND(( count(*)/ ( SELECT Count(*) FROM sd_event sde WHERE 1 = 1
AND tunnel_id = #{tunnelId}
) * 100 ), 3 ), 2 ) AS percentage FROM sd_event sde WHERE 1 = 1
AND tunnel_id = #{tunnelId}
GROUP BY event_state ) es ON sdd.dict_value = es.eventState WHERE 1 = 1 AND sdd.dict_type = 'sd_event_state'

SELECT se.event_state AS eventState, count(*) AS eventNumber FROM sd_event se WHERE 1 = 1 AND DATE_FORMAT( se.event_time, '%Y-%m-%d' ) = DATE_FORMAT( NOW(), '%Y-%m-%d' )
AND se.tunnel_id = #{tunnelId}
GROUP BY se.event_state

SELECT sfl.fallt_remove_statue AS falltRemoveStatue, count(*) eventNumber FROM sd_fault_list sfl WHERE 1 = 1 AND DATE_FORMAT( sfl.fault_fxtime, '%Y-%m-%d' ) = DATE_FORMAT( NOW(), '%Y-%m-%d' )
AND sfl.tunnel_id = #{tunnelId}
GROUP BY sfl.fallt_remove_statue

SELECT se.event_state AS eventState, count(*) eventNumber FROM sd_event se WHERE 1 = 1 AND DATE_FORMAT( se.event_time, '%Y-%m-%d' ) >= DATE_FORMAT( CONCAT( SUBDATE( CURDATE(), DATE_FORMAT( CURDATE(), '%w' )- 1 )), '%Y-%m-%d' ) AND DATE_FORMAT( se.event_time, '%Y-%m-%d' ) <= DATE_FORMAT( CONCAT( SUBDATE( CURDATE(), DATE_FORMAT( CURDATE(), '%w' )- 7 )), '%Y-%m-%d' )
AND se.tunnel_id = #{tunnelId}
GROUP BY se.event_state

SELECT sfl.fallt_remove_statue AS falltRemoveStatue, count(*) eventNumber FROM sd_fault_list sfl WHERE 1 = 1 AND DATE_FORMAT( sfl.fault_fxtime, '%Y-%m-%d' ) >= DATE_FORMAT( CONCAT( SUBDATE( CURDATE(), DATE_FORMAT( CURDATE(), '%w' )- 1 )), '%Y-%m-%d' ) AND DATE_FORMAT( sfl.fault_fxtime, '%Y-%m-%d' ) <= DATE_FORMAT( CONCAT( SUBDATE( CURDATE(), DATE_FORMAT( CURDATE(), '%w' )- 7 )), '%Y-%m-%d' )
AND sfl.tunnel_id = #{tunnelId}
GROUP BY sfl.fallt_remove_statue

SELECT IFNULL( tab.num, 0 ) AS eventNumber FROM ( SELECT @cdate := date_add( @cdate, INTERVAL - 1 DAY ) AS DATE FROM ( SELECT @cdate := date_add( CURDATE(), INTERVAL + 1 DAY ) FROM sd_event ) d1 WHERE YEAR ( @cdate )= YEAR (CURDATE()) AND MONTH ( @cdate )= MONTH (CURDATE()) AND DAY ( @cdate ) > 1 ORDER BY DATE ) date_c LEFT JOIN ( SELECT COUNT(*) AS num, DATE ( se.event_time ) AS t FROM sd_event se LEFT JOIN sd_event_type et ON se.event_type_id = et.id LEFT JOIN sys_dict_data sdd ON et.prev_control_type = sdd.dict_value AND sdd.dict_type = #{prevControlType} WHERE YEAR ( se.event_time )= YEAR (CURDATE()) AND MONTH ( se.event_time )= MONTH (CURDATE()) AND sdd.dict_value = #{dictValue} AND et.is_usable = '1'
AND se.tunnel_id = #{tunnelId}
GROUP BY DATE ( se.event_time ) ) tab ON DATE ( t )= DATE ORDER BY DATE ASC;

SELECT IFNULL( tab.num, 0 ) AS eventNumber FROM ( SELECT @cdate := date_add( @cdate, INTERVAL - 1 DAY ) AS DATE FROM ( SELECT @cdate := date_add( CURDATE(), INTERVAL + 1 DAY ) FROM sd_event ) d1 WHERE YEAR ( @cdate )= YEAR (CURDATE()) AND MONTH ( @cdate )= MONTH (CURDATE()) AND DAY ( @cdate ) > 1 ORDER BY DATE ) date_c LEFT JOIN ( SELECT COUNT(*) AS num, DATE ( se.event_time ) AS t FROM sd_event se LEFT JOIN sd_event_type et ON se.event_type_id = et.id LEFT JOIN sys_dict_data sdd ON et.prev_control_type = sdd.dict_value AND sdd.dict_type = #{prevControlType} WHERE YEAR ( se.event_time )= YEAR (CURDATE()) AND MONTH ( se.event_time )= MONTH (CURDATE()) AND sdd.dict_value = #{dictValue} AND et.is_usable = '1'
AND se.tunnel_id = #{tunnelId}
GROUP BY DATE ( se.event_time ) ) tab ON DATE ( t )= DATE ORDER BY DATE ASC;

SELECT IFNULL( tab.num, 0 ) AS eventNumber FROM ( SELECT @cdate := date_add( @cdate, INTERVAL - 1 DAY ) AS DATE FROM ( SELECT @cdate := date_add( CURDATE(), INTERVAL + 1 DAY ) FROM sd_event ) d1 WHERE YEAR ( @cdate )= YEAR (CURDATE()) AND MONTH ( @cdate )= MONTH (CURDATE()) AND DAY ( @cdate ) > 1 ORDER BY DATE ) date_c LEFT JOIN ( SELECT COUNT(*) AS num, DATE ( sfl.fault_fxtime ) AS t FROM sd_fault_list sfl WHERE YEAR ( sfl.fault_fxtime )= YEAR (CURDATE()) AND MONTH ( sfl.fault_fxtime )= MONTH (CURDATE())
AND sfl.tunnel_id = #{tunnelId}
GROUP BY DATE ( sfl.fault_fxtime ) ) tab ON DATE ( t )= DATE ORDER BY DATE ASC;

SELECT DATE_FORMAT(se.event_time,'%Y-%m-%d %H:%i:%S') AS eventTime, et.event_type AS eventType, sdd.dict_label AS eventState, t.tunnel_name AS tunnelName FROM sd_event se LEFT JOIN sd_event_type et ON se.event_type_id = et.id LEFT JOIN sd_tunnels t ON se.tunnel_id = t.tunnel_id LEFT JOIN sys_dict_data sdd ON se.event_state = sdd.dict_value AND sdd.dict_type = #{sdEventState} LEFT JOIN sys_dict_data sdd1 ON et.prev_control_type = sdd1.dict_value AND sdd1.dict_type = #{prevControlType} WHERE sdd1.dict_value IN ( #{dictValue1}, #{dictValue2} ) AND et.is_usable = '1'
AND se.tunnel_id = #{tunnelId}
UNION ALL SELECT sfl.fault_fxtime AS eventTime, sdd1.dict_label AS eventType, sdd2.dict_label AS eventState, t.tunnel_name AS tunnelName FROM sd_fault_list sfl LEFT JOIN sd_tunnels t ON sfl.tunnel_id = t.tunnel_id LEFT JOIN sys_dict_data sdd1 ON sfl.fault_type = sdd1.dict_value AND sdd1.dict_type = #{faultType} LEFT JOIN sys_dict_data sdd2 ON sfl.fallt_remove_statue = sdd2.dict_value AND sdd2.dict_type = #{faultRemoveStatue} WHERE 1 = 1
AND sfl.tunnel_id = #{tunnelId}
LIMIT 50

SELECT event_title AS eventTitle FROM sd_event se LEFT JOIN sd_event_type et ON se.event_type_id = et.id LEFT JOIN sys_dict_data sdd ON et.prev_control_type = sdd.dict_value AND sdd.dict_type = #{prevControlType} WHERE sdd.dict_value = #{dictValue} AND et.is_usable = '1'
AND se.tunnel_id = #{tunnelId}
LIMIT 50

SELECT event_title AS eventTitle FROM sd_event se LEFT JOIN sd_event_type et ON se.event_type_id = et.id LEFT JOIN sys_dict_data sdd ON et.prev_control_type = sdd.dict_value AND sdd.dict_type = #{prevControlType} WHERE sdd.dict_value = #{dictValue} AND et.is_usable = '1'
AND se.tunnel_id = #{tunnelId}
LIMIT 50

SELECT CONCAT( st.tunnel_name, '-', sdd.dict_label, '-', d.eq_name, '-', sdd1.dict_label ) AS faultInformation FROM sd_fault_list sfl LEFT JOIN sd_tunnels st ON sfl.tunnel_id = st.tunnel_id LEFT JOIN sd_devices d ON sfl.eq_id = d.eq_id LEFT JOIN sys_dict_data sdd ON d.eq_direction = sdd.dict_value AND sdd.dict_type = #{sdDirection} LEFT JOIN sys_dict_data sdd1 ON sfl.fault_type = sdd1.dict_value AND sdd1.dict_type = #{faultType} WHERE 1 = 1
AND sfl.tunnel_id = #{tunnelId}
LIMIT 50

SELECT e.event_title AS eventTitle, DATE_FORMAT(e.event_time,'%Y-%m-%d %H:%i:%S') AS startTime, IFNULL(e.video_url,'') AS videoUrl FROM sd_event e WHERE 1 = 1
AND e.tunnel_id = #{tunnelId}
ORDER BY e.event_time DESC LIMIT 50

SELECT e.event_type_id AS eventType, et.simplify_name AS typeName, count( e.event_type_id ) AS eventPercentage FROM sd_event e LEFT JOIN sd_event_type et ON e.event_type_id = et.id WHERE 1 = 1
AND e.tunnel_id = #{tunnelId}
GROUP BY e.event_type_id UNION ALL SELECT '' AS eventType, '设备故障' AS typeName, count(*) AS eventPercentage FROM sd_fault_list fl WHERE 1 = 1
AND fl.tunnel_id = #{tunnelId}
