mapper.select:
SELECT IFNULL( sum( tbr.totalnum ), 0 ) AS totalnum FROM ( SELECT @s := @s + 1 AS _index, DATE ( DATE_SUB( CURRENT_DATE, INTERVAL @s DAY )) AS _date FROM information_schema.TABLES,( SELECT @s := - 1 ) temp WHERE @s < 29 ORDER BY _date ) AS tbl LEFT JOIN ( SELECT count(*) AS totalnum, DATE ( CASE WHEN end_time IS NULL THEN ( CASE WHEN update_time IS NULL THEN event_time ELSE update_time END ) ELSE end_time END ) event_time FROM sd_event WHERE 1 = 1
AND tunnel_id = #{tunnelId}
GROUP BY DATE ( CASE WHEN end_time IS NULL THEN ( CASE WHEN update_time IS NULL THEN event_time ELSE update_time END ) ELSE end_time END ) ) AS tbr ON tbl._date = tbr.event_time GROUP BY tbl._date

SELECT tbl._date AS warningDate, IFNULL(sum(tbr.totalnum), 0) AS warningNum FROM ( SELECT @s :=@s + 1 AS _index,DATE(DATE_SUB(CURRENT_DATE, INTERVAL @s DAY)) AS _date FROM information_schema.tables,(SELECT @s := - 1) temp WHERE @s < 29 ORDER BY _date ) AS tbl LEFT JOIN ( SELECT count(*) AS totalnum, DATE ( CASE WHEN end_time IS NULL THEN ( CASE WHEN update_time IS NULL THEN event_time ELSE update_time END ) ELSE end_time END ) event_time FROM sd_event WHERE 1 = 1
AND tunnel_id = #{tunnelId}
and date_format(CASE WHEN end_time IS NULL THEN ( CASE WHEN update_time IS NULL THEN event_time ELSE update_time END ) ELSE end_time END,'%y%m%d') >= date_format(#{startDate},'%y%m%d')
and date_format(CASE WHEN end_time IS NULL THEN ( CASE WHEN update_time IS NULL THEN event_time ELSE update_time END ) ELSE end_time END,'%y%m%d') <= date_format(#{endDate},'%y%m%d')
GROUP BY DATE ( CASE WHEN end_time IS NULL THEN ( CASE WHEN update_time IS NULL THEN event_time ELSE update_time END ) ELSE end_time END ) ) AS tbr ON tbl._date = tbr.event_time WHERE 1=1
AND tbl._date >= #{startDate}
AND tbl._date <= #{endDate}
GROUP BY tbl._date
