mapper.select:
select detect.id,
detect.vehicle_id as vehicleId,
detect.tunnel_id as tunnelId,
detect.vehicle_type as vehicleType,
detect.vehicle_color as vehicleColor,
detect.longitude,
detect.latitude,
detect.speed,
detect.lane_num as laneNum,
detect.course_angle as courseAngle,
detect.vehicle_license as vehicleLicense,
detect.license_color as licenseColor,
detect.stake_num as stakeNum,
detect.detect_time as detectTime,
tunnul.tunnel_name as tunnulName
from sd_radar_detect_data detect
left join sd_tunnels tunnul on detect.tunnel_id = tunnul.tunnel_id

    1=1
    and detect.vehicle_id = #{vehicleId}
    and detect.tunnel_id = #{tunnelId}
    and detect.vehicle_type = #{vehicleType}
    and detect.vehicle_color = #{vehicleColor}
    and detect.longitude = #{longitude}
    and detect.latitude = #{latitude}
    and detect.speed = #{speed}
    and detect.lane_num = #{laneNum}
    and detect.course_angle = #{courseAngle}
    and detect.vehicle_license = #{vehicleLicense}
    and detect.license_color = #{licenseColor}
    and detect.stake_num = #{stakeNum}
    and detect.detect_time = #{detectTime}


        and date_format(detect.detect_time,'%y%m%d') >= date_format(#{startTime},'%y%m%d')


        and date_format(detect.detect_time,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and date(detect.detect_time) = curdate()


        AND (tunnul.dept_id = #{deptId} OR tunnul.dept_id IN ( SELECT d.dept_id FROM sys_dept d WHERE FIND_IN_SET (#{deptId},d.ancestors) ))


order by detect.detect_time desc

mapper.update:
update sd_radar_detect_data
vehicle_id = #{vehicleId}, tunnel_id = #{tunnelId}, vehicle_type = #{vehicleType}, vehicle_color = #{vehicleColor}, longitude = #{longitude}, latitude = #{latitude}, speed = #{speed}, lane_num = #{laneNum}, course_angle = #{courseAngle}, vehicle_license = #{vehicleLicense}, license_color = #{licenseColor}, stake_num = #{stakeNum}, detect_time = #{detectTime},
where id = #{id}

mapper.select:
SELECT  * FROM  sd_radar_detect_data
 where vehicle_id = #{vehicleId}
 order by detect_time ASC
