mapper.select:
select s.id, s.tunnel_id, s.fault_location, s.fault_type, s.fault_source, s.fault_fxtime, s.fault_cxtime, s.fault_tbr, s.fault_tbtime, a.eq_name, s.eq_id, s.eq_status, s.fault_code, s.fault_level, s.fallt_remove_statue, s.fault_description, fault_status
from sd_fault_list s left join sd_devices a on a.eq_id = s.eq_id

     and s.tunnel_id = #{tunnelId}
     and s.fault_location = #{faultLocation}
     and s.fault_type = #{faultType}
     and s.fault_source = #{faultSource}
     and s.fault_fxtime = #{faultFxtime}
     and s.fault_cxtime = #{faultCxtime}
     and s.fault_tbr = #{faultTbr}
     and s.fault_tbtime = #{faultTbtime}
     and s.eq_id = #{eqId}
     and s.eq_status = #{eqStatus}
     and s.fault_code = #{faultCode}
     and s.fault_level = #{faultLevel}
     and s.fallt_remove_statue = #{falltRemoveStatue}
     and s.fault_description = #{faultDescription}
     and s.fault_status = #{faultStatus}

order by s.create_time desc

mapper.update:
update sd_fault_list
tunnel_id = #{tunnelId}, fault_location = #{faultLocation}, fault_type = #{faultType}, fault_source = #{faultSource}, fault_fxtime = #{faultFxtime}, fault_cxtime = #{faultCxtime}, fault_tbr = #{faultTbr}, fault_tbtime = #{faultTbtime}, eq_id = #{eqId}, eq_status = #{eqStatus}, eq_run_status = #{eqRunStatus}, fault_code = #{faultCode}, fault_level = #{faultLevel}, fallt_remove_statue = #{falltRemoveStatue}, fault_description = #{faultDescription}, fault_status = #{faultStatus},
where id = #{id}

mapper.select:
select d.id,d.fault_location,v.eq_name,s.dict_label eq_direction,t.tunnel_name,d.fault_description from sd_fault_list d left join sd_devices v on d.eq_id = v.eq_id left join sys_dict_data s on d.fault_type = s.dict_value left join sd_tunnels t on t.tunnel_id = v.eq_tunnel_id
and s.dict_type= 'fault_type' and d.id in
#{item}

SELECT yemont.DATE as months, fl.eqFaultName AS eqSystemName, IFNULL( fl.eqFaultNum, 0 ) AS eqFaultNum FROM ( SELECT DATE_FORMAT( adddate( DATE_SUB( CURDATE(), INTERVAL dayofyear( now()) - 1 DAY ), INTERVAL numlist.id - 1 MONTH ), '%Y-%m' ) AS 'date' FROM ( SELECT @xi := @xi + 1 AS id FROM ( SELECT 1 UNION SELECT 2 UNION SELECT 3 ) xc1, ( SELECT 1 UNION SELECT 2 UNION SELECT 3 UNION SELECT 4 ) xc2, ( SELECT @xi := 0 ) xc0 ) AS numlist ) yemont LEFT JOIN ( SELECT sdd.dict_label AS eqFaultName, count(*) AS eqFaultNum, sfl.fault_fxtime FROM sd_fault_list sfl LEFT JOIN sd_devices sd ON sfl.eq_id = sd.eq_id LEFT JOIN sd_equipment_type et ON sd.eq_type = et.type_id LEFT JOIN sys_dict_data sdd ON et.eq_system = sdd.dict_value AND sdd.dict_type = #{eqSystem} WHERE 1 = 1 AND et.eq_system = #{eqSystemType} AND sfl.fault_status = #{faultStatus}
AND sfl.tunnel_id = #{tunnelId}
GROUP BY et.eq_system ) fl ON yemont.DATE = DATE_FORMAT( fl.fault_fxtime, '%Y-%m' ) WHERE 1 = 1 ORDER BY yemont.DATE ASC

SELECT sdd.dict_label AS eqSystemName, count(*) AS eqFaultNum, sdd.dict_value AS eqSystemCode FROM sd_fault_list sfl LEFT JOIN sd_devices sd ON sfl.eq_id = sd.eq_id LEFT JOIN sd_equipment_type et ON sd.eq_type = et.type_id LEFT JOIN sys_dict_data sdd ON et.eq_system = sdd.dict_value AND sdd.dict_type = #{eqSystem} WHERE 1 = 1 AND sfl.fault_status = #{faultStatus}
AND sfl.tunnel_id = #{tunnelId}
AND sfl.fallt_remove_statue = #{falltRemoveStatue} GROUP BY et.eq_system
