mapper.select:
select t.id,t.task_name, s.tunnel_name, t.end_plantime, u.nick_name dispatcher,t.dispatch_time, b.dept_name bzName,t.bz_id, t.task_description, t.publish_status, t.task_status, w.nick_name walker_id, t.task_endtime, t.task_cxtime, t.site_description, t.create_by, t.create_time, t.update_by, t.update_time,p.dict_label publish,a.dict_label task from sd_task_list t
left join sys_user u on t.dispatcher = u.user_id
left join sd_teams b on b.dept_id =  t.bz_id
left join sys_user w on t.walker_id = w.user_id
left join sd_tunnels s on s.tunnel_id = t.tunnel_id
left join sys_dict_data p on p.dict_value= t.publish_status and p.dict_type = 'publish_status'
left join sys_dict_data a on a.dict_value= t.task_status and a.dict_type = 'task_status'

    1=1 and t.task_status!= "3"
     and b.dept_name like concat('%', #{zzjgId}, '%')
     and t.end_plantime = #{endPlantime}
     and t.dispatcher = #{dispatcher}
     and t.dispatch_time = #{dispatchTime}
     and t.bz_id = #{bzId}
     and s.tunnel_id = #{tunnelId}
     and t.task_description = #{taskDescription}
     and t.publish_status = #{publishStatus}
     and t.task_status = #{taskStatus} 
     and t.walker_id = #{walkerId}
     and t.task_endtime = #{taskEndtime}
     and t.task_cxtime = #{taskCxtime}
     and t.site_description = #{siteDescription}
     and FIND_IN_SET(t.id,#{ids}) > 0

        AND (s.dept_id = #{deptId} OR s.dept_id IN ( SELECT q.dept_id FROM sys_dept q WHERE FIND_IN_SET (#{deptId},q.ancestors) ))

     and t.end_plantime < now() and (t.task_status in(0,1) or t.task_status = '2,4')
     and (t.task_status = '2,4' or t.task_status = '2')

        and t.end_plantime >= #{params.beginTime}


        and t.end_plantime <= #{params.endTime}



order  by  t.create_time desc,t.update_time desc

select t.id, s.tunnel_name,t.end_plantime, u.nick_name dispatcher,t.dispatch_time, b.dept_name bzName,t.bz_id, t.task_description, t.publish_status, t.task_status, w.nick_name walker_id, t.task_endtime, t.task_cxtime, t.site_description, t.create_by, t.create_time, t.update_by, t.update_time,p.dict_label publish,a.dict_label task from sd_task_list t
left join sys_user u on t.dispatcher = u.user_id
left join sd_teams b on b.dept_id =  t.bz_id
left join sys_user w on t.walker_id = w.user_id
left join sd_tunnels s on s.tunnel_id = t.tunnel_id
left join sys_dict_data p on p.dict_value= t.publish_status and p.dict_type = 'publish_status'
left join sys_dict_data a on a.dict_value= t.task_status and a.dict_type = 'task_status'

    1=1
     and b.dept_name like concat('%', #{zzjgId}, '%')
     and t.end_plantime = #{endPlantime}
     and t.dispatcher = #{dispatcher}
     and t.dispatch_time = #{dispatchTime}
     and t.bz_id = #{bzId}
     and t.task_description = #{taskDescription}
     and t.publish_status = #{publishStatus}
     and t.task_status in ('0','1')
     and t.walker_id = #{walkerId}
     and t.task_endtime = #{taskEndtime}
     and t.task_cxtime = #{taskCxtime}
     and t.site_description = #{siteDescription}
     and FIND_IN_SET(t.id,#{ids}) > 0

        AND (s.dept_id = #{deptId} OR s.dept_id IN ( SELECT q.dept_id FROM sys_dept q WHERE FIND_IN_SET (#{deptId},q.ancestors) ))


order  by  t.create_time desc,t.update_time desc

mapper.update:
update sd_task_list
zzjg_id = #{zzjgId}, tunnel_id = #{tunnelId}, task_name = #{taskName}, end_plantime = #{endPlantime}, dispatcher = #{dispatcher}, dispatch_time = #{dispatchTime}, bz_id = #{bzId}, task_description = #{taskDescription}, publish_status = #{publishStatus}, task_status = #{taskStatus}, walker_id = #{walkerId}, task_endtime = #{taskEndtime}, task_cxtime = #{taskCxtime}, site_description = #{siteDescription}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = SYSDATE(),
where id = #{id}

update sd_task_list
zzjg_id = #{zzjgId}, end_plantime = #{endPlantime}, dispatcher = #{dispatcher}, dispatch_time = #{dispatchTime}, bz_id = #{bzId}, task_description = #{taskDescription}, publish_status = #{publishStatus}, task_status =#{taskStatus}, finish_status =#{finishStatus}, task_endtime = #{taskEndtime}, task_cxtime = #{taskCxtime}, site_description = #{siteDescription}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}
