mapper.select:
select es.tunnel_id id,es.energy_value value,CASE es.statistics_type WHEN 0 THEN DATE_FORMAT(es.create_time,'%Y-%m-%d %H') WHEN 1 THEN DATE_FORMAT(es.create_time,'%Y-%m-%d') WHEN 2 THEN DATE_FORMAT(es.create_time,'%Y-%m') END AS rt,st.tunnel_name name from energy_site es left join sd_tunnels st on es.tunnel_id = st.tunnel_id where es.tunnel_id in
#{item}
and es.statistics_type = #{statisticsType} and es.create_time >= DATE_FORMAT(#{statisticsDate},
'%Y-%m-%d' '%Y-%m' '%Y'
) and es.create_time < DATE_FORMAT( date_add( #{statisticsDate},
INTERVAL 1 DAY ) , '%Y-%m-%d' interval 1 month), '%Y-%m' interval 1 year), '%Y'
)

select ei.itemized_code id,ei.energy_value value,CASE ei.statistics_type WHEN 0 THEN DATE_FORMAT(ei.create_time,'%Y-%m-%d %H') WHEN 1 THEN DATE_FORMAT(ei.create_time,'%Y-%m-%d') WHEN 2 THEN DATE_FORMAT(ei.create_time,'%Y-%m') END AS rt,eci.itemized_name name from energy_item ei left join energy_configcenter_itemized eci on ei.itemized_code = eci.itemized_code where ei.itemized_code in
#{item}
and ei.tunnel_id in
#{item1}
and ei.statistics_type = #{statisticsType} and ei.create_time >= DATE_FORMAT(#{statisticsDate},
'%Y-%m-%d' '%Y-%m' '%Y'
) and ei.create_time <DATE_FORMAT( date_add( #{statisticsDate},
INTERVAL 1 DAY ) , '%Y-%m-%d' interval 1 month), '%Y-%m' interval 1 year), '%Y'
)

select ei.classification_code id,ei.energy_value value,CASE ei.statistics_type WHEN 0 THEN DATE_FORMAT(ei.create_time,'%Y-%m-%d %H') WHEN 1 THEN DATE_FORMAT(ei.create_time,'%Y-%m-%d') WHEN 2 THEN DATE_FORMAT(ei.create_time,'%Y-%m') END AS rt,eci.classification_name name from energy_classification ei left join energy_configcenter_classification eci on ei.classification_code = eci.classification_code where ei.classification_code in
#{item}
and ei.tunnel_id in
#{item1}
and ei.statistics_type = #{statisticsType} and ei.create_time >= DATE_FORMAT(#{statisticsDate},
'%Y-%m-%d' '%Y-%m' '%Y'
) and ei.create_time <DATE_FORMAT( date_add( #{statisticsDate},
INTERVAL 1 DAY ) , '%Y-%m-%d' interval 1 month), '%Y-%m' interval 1 year), '%Y'
)

select ef.source source,ef.target target,ef.value value from energy_footprint ef where ef.tunnel_id in
#{item}
and ef.statistics_type = #{statisticsType} and ef.create_time >= DATE_FORMAT(#{baseTime},
'%Y-%m-%d' '%Y-%m' '%Y'
) and ef.create_time <DATE_FORMAT( date_add( #{baseTime},
INTERVAL 1 DAY ) , '%Y-%m-%d' interval 1 month), '%Y-%m' interval 1 year), '%Y'
)

select ef.tunnel_id tunnelId,st.tunnel_name tunnelName,ef.jValue jValue,ef.fValue fValue,ef.pValue pValue,ef.gValue gValue,ef.sValue sValue from energy_dayparting ef left join sd_tunnels st on ef.tunnel_id = st.tunnel_id where ef.tunnel_id in
#{item}
and ef.statistics_type = #{statisticsType} and ef.create_time >= DATE_FORMAT(#{baseTime},
'%Y-%m-%d' '%Y-%m' '%Y'
) and ef.create_time <DATE_FORMAT( date_add( #{baseTime},
INTERVAL 1 DAY ) , '%Y-%m-%d' interval 1 month), '%Y-%m' interval 1 year), '%Y'
)

select id id,unit_price2 unitPrice2,unit_price3 unitPrice3,unit_price4 unitPrice4,unit_price5 unitPrice5,unit_price6 unitPrice6
,jian_cof jianCof,feng_cof fengCof,gu_cof guCof,shen_cof shenCof,month month
 from energy_configcenter_electricity_price where `month` = DATE_FORMAT(#{month}, '%Y-%m')

SELECT ea.dept_code AS deptCode, st.tunnel_name AS tunnelName, DATE_FORMAT( ea.statistics_date,
'%Y-%m-%d' '%Y-%m'
) AS rt, ea.split_time_type AS splitTimeType, sum( ea.`value` ) AS value FROM energy_analysis_electricity_bill ea LEFT JOIN sd_tunnels st ON ea.dept_code = st.tunnel_id WHERE ea.dept_code in
#{item}
AND ea.statistics_type = #{statisticsType} AND ea.statistics_date >= DATE_FORMAT(#{baseTime},
'%Y-%m' '%Y'
) AND ea.statistics_date < DATE_FORMAT( date_add( #{baseTime},
interval 1 month), '%Y-%m' interval 1 year), '%Y'
) GROUP BY ea.split_time_type,ea.dept_code,DATE_FORMAT( ea.statistics_date,
'%Y-%m-%d' '%Y-%m'
)
