mapper.select:
select A.id, A.tunnel_id, A.event_type_id, A.event_title,A.event_source, A.event_time, A.event_state, A.event_grade,
A.event_location, A.event_death, A.event_injured,A.slight_injured, A.event_description, A.reserve_plan_id,
A.flow_id, A.warning_id,A.station_id, A.lane_no,A.longitude, A.latitude, A.stake_num, A.start_time, A.end_time,A.video_url,
A.create_by, A.create_time, A.update_by, A.update_time,A.direction,A.small_car_num,A.truck_num,A.passenger_car_num,A.tanker_num,
A.car_owner_phone, A.wrecker_phone,A.police_phone,A.remark,
B.event_type,C.tunnel_name, C.tunnel_station_name
from sd_event A
left join sd_event_type B
on A.event_type_id = B.id
left join sd_tunnels C on A.tunnel_id = C.tunnel_id

    1=1
    and A.id = #{id}

    and C.dept_id = #{deptId}
    and A.tunnel_id = #{tunnelId}
    and A.event_type_id = #{eventTypeId}
    and A.event_title = #{eventTitle}
    and A.event_time = #{eventTime}
    and A.event_state = #{eventState}
    and A.event_grade = #{eventGrade}
    and A.event_location = #{eventLocation}
    and A.event_death = #{eventDeath}
    and A.event_injured = #{eventInjured}
    and A.event_description =
        #{eventDescription}



        and date_format(A.start_time,'%y%m%d') >= date_format(#{startTime},'%y%m%d')


        and date_format(A.end_time,'%y%m%d') <= date_format(#{endTime},'%y%m%d')

    order by A.start_time desc

mapper.update:
update sd_event
tunnel_id = #{tunnelId}, event_type_id = #{eventTypeId}, event_title = #{eventTitle}, event_time = #{eventTime}, event_state = #{eventState}, event_grade = #{eventGrade}, event_location = #{eventLocation}, event_death = #{eventDeath}, event_injured = #{eventInjured}, event_description = #{eventDescription}, reserve_plan_id = #{reservePlanId}, flow_id = #{flowId}, warning_id = #{warningId}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, police_phone = #{policePhone}, wrecker_phone = #{wreckerPhone}, car_owner_phone = #{carOwnerPhone}, tanker_num = #{tankerNum}, passenger_car_num = #{passengerCarNum}, truck_num = #{truckNum}, small_car_num = #{smallCarNum}, direction = #{direction}, slight_injured = #{slightInjured}, event_source = #{eventSource},
where id = #{id}

update sd_event
set video_url=#{eventVideoUrl}
where id = #{eventId}

mapper.select:
select A.id, A.tunnel_id, A.event_type_id, A.event_title, A.event_time, A.event_state, A.event_grade, A.event_location, A.event_death, A.event_injured, A.event_description, A.reserve_plan_id, A.flow_id, A.warning_id, A.create_by, A.create_time, A.update_by, A.update_time, B.event_type, C.tunnel_name, A.remark, A.police_phone, A.wrecker_phone, A.car_owner_phone, A.tanker_num, A.passenger_car_num, A.small_car_num, A.direction, A.slight_injured, A.truck_num, A.start_time, A.end_time,A.stake_num,C.tunnel_station_name from sd_event A left join sd_event_type B on A.event_type_id = B.id left join sd_tunnels C on A.tunnel_id = C.tunnel_id where TO_DAYS(A.start_time) = TO_DAYS(NOW()) and A.event_state = #{eventState}
AND (C.dept_id = #{params.deptId} OR C.dept_id IN ( SELECT d.dept_id FROM sys_dept d WHERE FIND_IN_SET (#{params.deptId},d.ancestors) ))
order by A.start_time desc
