mapper.select:
		select ide.device_id, ide.device_name, ide.brand_id,e.brand_name,ida.device_ip,ida.device_port,ida.user_name,
		       ida.pass_word,ide.route_id, ide.route_direction, ide.device_marking_name, ide.device_type_id,
		       ide.device_type_number, ide.device_model_id, ide.factory_library, ide.user_unit_id, ide.operator_id,
		       ide.longitude, ide.latitude, ide.firm_id, ide.purchase_date, ide.warranty_years, ide.unit_price,
		       ide.run_status,ide.device_status, ide.install_date, ide.maintain_id, ide.collar_agency_id,
		       ide.pile_number, ide.manage_agency_id, ide.producte_date, ide.repair_date, ide.operate_date,
		       ide.service_life, ide.storage_date, ide.is_monitor,ide.is_config, ide.local_info, ida.device_pixel, ide.tunnel_id
		from iot_device ide
        left join iot_device_access ida on ida.device_id = ide.device_id
        left join sys_dept d on d.dept_id=ide.manage_agency_id
        left join iot_brand e on e.brand_id=ide.brand_id

             and device_name like concat('%', #{deviceName}, '%')
             and brand_id = #{brandId}
             and route_id = #{routeId}
             and ide.device_id = #{deviceId}
             and route_direction = #{routeDirection}
             and device_marking_name like concat('%', #{deviceMarkingName}, '%')
             and device_type_number = #{deviceTypeNumber}
             and device_type_id = #{deviceTypeId}
             and device_model_id = #{deviceModelId}
             and factory_library = #{factoryLibrary}
             and user_unit_id = #{userUnitId}
             and operator_id = #{operatorId}
             and longitude = #{longitude}
             and latitude = #{latitude}
             and firm_id = #{firmId}
             and purchase_date = #{purchaseDate}
             and warranty_years = #{warrantyYears}
             and unit_price = #{unitPrice}
             and run_status = #{runStatus}
             and device_status = #{deviceStatus}
             and install_date = #{installDate}
             and maintain_id = #{maintainId}
             and collar_agency_id = #{collarAgencyId}
             and pile_number = #{pileNumber}
             and producte_date = #{producteDate}
             and repair_date = #{repairDate}
             and operate_date = #{operateDate}
             and service_life = #{serviceLife}
             and storage_date = #{storageDate}
             and is_monitor = #{isMonitor}
             and is_config = #{isConfig}
             and local_info = #{localInfo}
             and ida.device_pixel = #{devicePixel}
             and ide.tunnel_id = #{tunnelId}

select ide.device_id,ida.device_ip,ida.device_port,ida.user_name,ida.pass_word,ide.route_id,ide.route_direction,ide.pile_number,ide.device_type_id
from iot_device ide
left join iot_device_access ida on ida.device_id = ide.device_id

    and ide.device_type_id in (${deviceTypeId})
    and ide.device_type_number in (${deviceTypeNumber})
    and is_monitor = #{isMonitor}
    and ide.device_id = #{deviceId}
    and manage_agency_id = #{manageAgencyId}

select device_id, device_name, ide.device_type_id,device_type_number,route_direction,pile_number, longitude,
latitude,
device_status,ir.route_number,ir.route_name
from iot_device ide
left join iot_route ir on ide.route_id = ir.route_id

    and ide.device_type_id = #{deviceTypeId}
    and manage_agency_id = #{manageAgencyId}
    and device_status in ${deviceStatus}

SELECT SUM(CASE WHEN device_status = '0' THEN 1 ELSE 0 END) AS ZC,SUM(CASE WHEN device_status = '1' THEN 1 ELSE 0 END) AS LX FROM iot_device


        and manage_agency_id=#{manageAgencyId}


    and device_type_id in(${deviceTypeIds})


    and route_id=#{routeId}


      and  device_status in ${deviceStatus}

SELECT a.device_pixel from iot_device_access a
left join iot_device b on a.device_id = b.device_id
where a.device_pixel is not null
and b.tunnel_id = #{tunnelId}
and b.local_info = #{localInfo}
GROUP BY a.device_pixel
