mapper.select:
select s.id, s.tunnel_id, s.statistic_time, s.direction, s.traffic_flow, s.average_velocity,s.line_occupancy, s.car_num,
s.trunk_num, s.special_car_num, s.create_time, s.create_by, s.update_time, s.update_by,t.tunnel_name
from sd_traffic_flow_state s
left join sd_tunnels t on s.tunnel_id = t.tunnel_id

     and s.tunnel_id = #{tunnelId}

     and s.direction = #{direction}
     and s.traffic_flow = #{trafficFlow}
     and s.average_velocity = #{averageVelocity}
    and s.line_occupancy = #{lineOccupancy}
     and s.car_num = #{carNum}
     and s.trunk_num = #{trunkNum}
     and s.special_car_num = #{specialCarNum}


        and date_format(s.statistic_time,'%y%m%d') >= date_format(#{params.startTime},'%y%m%d')



        and date_format(s.statistic_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


        AND (t.dept_id = #{params.deptId} OR t.dept_id IN ( SELECT d.dept_id FROM sys_dept d WHERE FIND_IN_SET (#{params.deptId},d.ancestors) ))



order by s.statistic_time desc

select a.id, a.tunnel_id, a.statistic_time, a.direction, a.traffic_flow, a.average_velocity,a.line_occupancy, a.car_num, a.trunk_num,a.special_car_num from sd_traffic_flow_state a left join sd_tunnels b on b.tunnel_id = a.tunnel_id where a.statistic_time >= date_sub(sysdate(),interval 1 day) and a.tunnel_id = #{tunnelId}
AND (b.dept_id = #{deptId} OR b.dept_id IN ( SELECT d.dept_id FROM sys_dept d WHERE FIND_IN_SET (#{deptId},d.ancestors) ))
order by a.statistic_time asc
